import axios from "axios";
import { Button, Card, Text, Title } from 'react-native-paper';
import { useEffect, useState } from 'react';
import { FlatList } from "react-native";

export default function ArticleList() {

    const [APIData, setAPIData] = useState([]);



    useEffect(() => {
        axios.get('http://192.168.1.102:8000/api/article/list')
            .then((response) => {
                setAPIData(response);
                console.log(response.data);
            })
            .catch(function (error) {
                console.log(error);
            });
    }, [])

    const state = {
        article: [
            { title: 'Bois', articlesId: 1, content: 'Bois de charpente', image: 'https://www.centralbois.fr/406-large_default/planche-22x190mm-en-3m-douglas-rabote-naturel-c18.jpg' },
            { title: 'Beton', articlesId: 2, content: 'Beton dur', image: 'https://www.techni-contact.com/ressources/images/produits/thumb_pdt/borne-de-ville-en-beton-carree-2383523-1.jpg' },
            { title: 'Fenetre bois', articlesId: 3, content: 'Fenetre en bois', image: 'https://www.fenetres-lorenove.fr/storage/products/773-1572271653.jpg' },
            { title: 'Velux', articlesId: 4, content: 'Velux 200x300', image: 'https://www.toiture-online.com/media/catalog/product/cache/1/image/9df78eab33525d08d6e5fb8d27136e95/f/e/fenetre-de-toit-velux-rotation-ggu-0076_1.jpg' },
            { title: 'Cable electrique', articlesId: 5, content: 'Cable de 2mm de diametre', image: 'https://media.castorama.fr/is/image/Castorama/c-ble-lectrique-u1000r2v-3x2-5-mm-couronne-50-m~3427500438992_02c?$MOB_PREV$&$width=618&$height=618' },
            { title: 'Pot de peinture', articlesId: 6, content: 'Peinture blanche', image: 'https://www.luniversdupeintre.com/images/imagecache/350x350/jpg/peinture-acrylique-murale-Sigmalys-EVO-velours-15L.webp' },
            { title: 'Carrelage', articlesId: 7, content: 'Carrelage chester cream modular', image: 'https://img.tile.expert/img_lb/codicer-95/chester/per_sito/minimali/b_codicer_95_chester_chester_cream_modular_50x50.jpg?59' }
        ]
    }

    const renderItem = ({ item }) => (


        <Card style={{ marginTop: '10%' }}>
            <Card.Title title={item.title} subtitle={item.date} />
            <Card.Content>
                <Title>{item.content}</Title>
            </Card.Content>
            <Card.Cover source={{ uri: item.image }} />
            {/* quand l'image viens de la bdd */}
            {/* <Card.Cover source={{ uri: "192.168.1.102:8000" + item.image }} /> */}
            <Card.Actions>
                <Button>Modifier</Button>
                <Button>Supprimer</Button>
            </Card.Actions>
        </Card>

    );


    return (


        <FlatList
            data={state.article}
            renderItem={renderItem}
            keyExtractor={item => item.articlesId}
        />

    );


}
